
function initPytania(){jQuery("#pytania").accordion();jQuery("#pytania li").click(function(){jQuery(this).find(":radio").prop("checked",true).change()});jQuery("#pytania :radio").change(function(){var a=jQuery(this).parents("div").prevAll("h3").length;jQuery("#pytania").accordion("option","active",a)});jQuery("#quiz-form").submit(function(){var b=jQuery("#pytania ol").length;var a=jQuery("#pytania :radio:checked").length;if(a<b){alert("Należy udzielić odpowiedzi na wszystkie pytania!");return false}})}jQuery(document).ready(function(){initPytania()});